import React, { useEffect , useState} from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// import ProjectCard from './projectCard.js';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import {projectData} from '../../backend/pjtData';

function determineImg(imageName){
  switch(imageName) {
    case "Dasmoto":
      return <img src='../../assets/projectImgs/Dasmoto.png' alt="Dasmoto's Arts Crafts Website"/>
      break;
        default:
      // code block
  }
};
  

function ProjectGrid() {
  return (
    <Container>
      <Row>
        <Col sm={8}>
            {/* <ProjectCard /> */}
            {/* <div>
              {projectData.map((project) => (
                  <div key={projectData.id}> {project.id} </div>
                
              ))}
            </div> */}   
            <>
              <div>
                <h3>Using local JSON file</h3>
                {projectData.map((project) => (
                <Card style={{ width: '18rem' }}>
                      <Card.Img variant="top" src={determineImg(project.imgName)} />
                    <Card.Body>
                      <Card.Title>Card Title</Card.Title>
                      <Card.Text>
                        {project.key}
                    </Card.Text>
                    <Button variant="primary">Go somewhere</Button>
                  </Card.Body>
                </Card>

            ))}
            </div>
            </>


          {/* <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src="holder.js/100px180" />
            <Card.Body>
              <Card.Title>Card Title</Card.Title>
              <Card.Text>         
              </Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
           </Card> */}

        </Col>
        <Col sm={4}>sm=4</Col>
      </Row>
      <Row>
        <Col sm>sm=true</Col>
        <Col sm>sm=true</Col>
        <Col sm>sm=true</Col>
      </Row>
    </Container>
  );
}

export default ProjectGrid;
